# New TunePad cell

MAJ = [0, 4, 7]
MIN = [0, 3, 7]

def play_triad_block(root, quality, beats=2.0, vel=75):
    intervals = MAJ if quality == "maj" else MIN
    # hit three notes within the bar
    step = beats / 3.0
    for i in intervals:
        playNote(root + i, beats=step, velocity=vel)

def play_triad_arp(root, quality, beats=2.0, vel=75):
    intervals = MAJ if quality == "maj" else MIN
    step = beats / 3.0
    for i in intervals:
        playNote(root + i, beats=step, velocity=vel)

def play_progression(song="ode", style="block", cycles=2, vel=75):
    # roots one octave lower so they sit under the melody
    if song == "ode":
        prog = [(36, "maj", 2.0), (43, "maj", 2.0), (45, "min", 2.0), (41, "maj", 2.0)]
    elif song == "greensleeves":
        prog = [(45, "min", 2.0), (43, "maj", 2.0), (41, "maj", 2.0), (40, "maj", 2.0)]
    elif song == "twinkle":
        prog = [(36, "maj", 2.0), (41, "maj", 2.0), (43, "maj", 2.0), (36, "maj", 2.0)]
    else:
        prog = []

    for _ in range(cycles):
        for root, quality, b in prog:
            if style == "arp":
                play_triad_arp(root, quality, beats=b, vel=vel)
            else:
                play_triad_block(root, quality, beats=b, vel=vel)

